const M = 6364136223846793005n;
const C = 1442695040888963407n;

const cipherBytes = [
    184, 49, 100, 14, 84, 207, 101, 2, 75, 115, 221, 87, 230, 205, 69, 99, 
    99, 98, 44, 86, 225, 137, 134, 172, 195, 50, 10, 7, 243, 119, 102, 177, 
    183, 173, 50, 242, 213, 100, 211, 203, 92, 69, 153, 194, 137, 146
];

const targetPrefix = "UniCTF{";
// Pre-calculate target bytes for faster checking
const targetBytes = [];
for (let i = 0; i < targetPrefix.length; i++) {
    targetBytes.push(targetPrefix.charCodeAt(i));
}

function solve() {
    console.log("Starting optimized brute force...");
    const startTime = Date.now();

    // Ranges:
    // v1 (Length param): 8 to 40 (Corresponds to Length 50 to 250)
    // v2 (Speed): 1 to 100
    // v3 (Jitter Y): 0 to 200
    // v4 (Jitter X): 0 to 200

    // Total iterations: ~32 * 100 * 200 * 200 = 128,000,000.
    // This might take a minute in JS.

    let count = 0;

    for (let v1 = 8; v1 < 40; v1++) {
        // Optimization: Pre-shift v1
        const s1 = BigInt(v1) << 48n;

        for (let v2 = 1; v2 < 100; v2++) {
            const s2 = s1 | (BigInt(v2) << 32n);

            for (let v3 = 0; v3 < 200; v3++) {
                const s3 = s2 | (BigInt(v3) << 16n);

                for (let v4 = 0; v4 < 200; v4++) {
                    const seed = s3 | BigInt(v4);

                    // Check first byte manually for speed
                    // S_new = seed * M + C
                    // Byte = (S_new >> 56) ^ cipher[0]
                    // Expected = 'U'

                    let nextSeed = seed * M + C;
                    // Masking is needed for the multiplication result if we want true 64-bit emulation,
                    // but for >> 56 we only care about top bits.
                    // However, JS BigInt multiplication produces full result.
                    // We must truncate to 64 bits before shifting?
                    // The WASM mul is i64.mul which wraps.
                    // So yes, we must mask.

                    nextSeed = nextSeed & 0xFFFFFFFFFFFFFFFFn;

                    const b0 = Number(nextSeed >> 56n) ^ cipherBytes[0];
                    if (b0 !== 85) { // 'U' is 85
                        continue;
                    }

                    // If first byte matches, check the rest
                    if (checkRest(nextSeed)) {
                         console.log(`\nFOUND MATCH!`);
                         console.log(`v1=${v1}, v2=${v2}, v3=${v3}, v4=${v4}`);
                         console.log(`Initial Seed: ${seed}`);
                         const fullStr = decrypt(seed);
                         console.log(`Decrypted: ${fullStr}`);
                         console.log(`Time: ${(Date.now() - startTime)/1000}s`);

                         // Calculate Sum
                         // Biometric Sum = v2 + v3 + v4?
                         // Or v2 + (v3+v4)/2?
                         // We will list likely sums.
                         console.log(`Sum v2+v3+v4: ${v2+v3+v4}`);
                         return;
                    }
                }
            }
            // Progress report
            if (v2 % 20 === 0) {
                 // console.log(`Scanned v1=${v1}, v2=${v2}...`);
            }
        }
        console.log(`Finished v1=${v1}. Time: ${(Date.now() - startTime)/1000}s`);
    }
    console.log("No match found in these ranges.");
}

function checkRest(currentSeed) {
    // We already checked index 0. currentSeed is S_1.
    // Check indices 1 to 6 ("niCTF{")

    let seed = currentSeed;

    for (let i = 1; i < targetBytes.length; i++) {
        seed = seed * M + C;
        seed = seed & 0xFFFFFFFFFFFFFFFFn;

        const byte = Number(seed >> 56n) ^ cipherBytes[i];
        if (byte !== targetBytes[i]) {
            return false;
        }
    }
    return true;
}

function decrypt(seed) {
    let currentSeed = seed;
    let result = "";
    for (let i = 0; i < cipherBytes.length; i++) {
        currentSeed = currentSeed * M + C;
        currentSeed = currentSeed & 0xFFFFFFFFFFFFFFFFn;

        let xorKey = Number((currentSeed >> 56n) & 0xFFn);
        let charCode = cipherBytes[i] ^ xorKey;
        result += String.fromCharCode(charCode);
    }
    return result;
}

solve();
