def custom_encrypt(plaintext):
    encrypted = ""
    key = [3, 5, 2]
    key_index = 0
    for char in plaintext:
        if 'a' <= char <= 'z':
            shift = key[key_index]
            new_char = chr((ord(char) - ord('a') + shift) % 26 + ord('a'))
            key_index = (key_index + 1) % len(key)
        elif 'A' <= char <= 'Z':
            shift = key[key_index]
            new_char = chr((ord(char) - ord('A') + shift) % 26 + ord('A'))
            key_index = (key_index + 1) % len(key)
        elif '0' <= char <= '9':
            num = int(char)
            new_num = (num + 7) % 10
            new_char = str(new_num)
        else:
            new_char = char
        encrypted += new_char
    return encrypted


